import express from 'express';
import { twoFactorAuthController } from '../controllers/TwoFactorAuthController.js';
import { userAuth } from '../middlewares/userAuth.js';
const router = express.Router();
router.post('/2fa/setup', userAuth, (req, res, next) => twoFactorAuthController.setup(req, res, next));
router.post('/2fa/enable', userAuth, (req, res, next) => twoFactorAuthController.enable(req, res, next));
router.post('/2fa/disable', userAuth, (req, res, next) => twoFactorAuthController.disable(req, res, next));
router.get('/2fa/status', userAuth, (req, res, next) => twoFactorAuthController.status(req, res, next));
router.post('/2fa/verify', userAuth, (req, res, next) => twoFactorAuthController.verify(req, res, next));
router.post('/2fa/verify-recovery', userAuth, (req, res, next) => twoFactorAuthController.verifyRecovery(req, res, next));
router.post('/2fa/recovery-codes', userAuth, (req, res, next) => twoFactorAuthController.regenerateRecoveryCodes(req, res, next));
router.get('/2fa/audit-logs', userAuth, (req, res, next) => twoFactorAuthController.auditLogs(req, res, next));
export default router;
